'{
  "$schema": "https://vega.github.io/schema/vega/v5.json",
  "description": "A force-directed graph for a DAX Query Plan with Node and Edge Indices",
  // "width": 700,
  // "height": 500,
  "padding": 5,
  // "autosize": "none",
  "background": "#263238",
  "signals": [
    {"name": "cx", "update": "width / 2"},
    {"name": "cy", "update": "height / 2"},
    {"name": "nodeRadius", "value": 8},
    {"name": "nodeCharge", "value": -30},
    {"name": "linkDistance", "value": 30},
    {"name": "static", "value": false},
    {"name": "gravityX", "value": 0.2},
    {"name": "gravityY", "value": 0.9},
    {
      "description": "State variable for active node fix status.",
      "name": "fix",
      "value": false,
      "on": [
        {
          "events": "symbol:pointerout[!event.buttons], window:pointerup",
          "update": "false"
        },
        {"events": "symbol:pointerover", "update": "fix || true"},
        {
          "events": "[symbol:pointerdown, window:pointerup] > window:pointermove!",
          "update": "xy()",
          "force": true
        }
      ]
    },
    {
      "description": "Graph node most recently interacted with.",
      "name": "node",
      "value": null,
      "on": [
        {
          "events": "symbol:pointerover",
          "update": "fix === true ? item() : node"
        }
      ]
    },
    {
      "description": "Flag to restart Force simulation upon data changes.",
      "name": "restart",
      "value": false,
      "on": [{"events": {"signal": "fix"}, "update": "fix && fix.length"}]
    }
  ],
  "data": [
    {
      "name": "dataset"
    },
    {
      "name": "node-data",
      "source": "dataset",
      "transform": [
        {
          "type": "project",
          "fields": [
            "dstid",
            "operation",
            "level",
            "records",
            "operationType",
            "operationShort"
          ],
          "as": ["index", "Operation", "Level", "Records"]
        },
        {
          "type": "formula",
          "expr": "toNumber(datum.index)",
          "as": "index"
        },
        {
          "type": "formula",
          "expr": "toNumber(datum.srcid)",
          "as": "dstid"
        },
        {
          "type": "formula",
          "expr": "toNumber(datum.dstid)",
          "as": "dstid"
        },
        {
          "type": "formula",
          "expr": "length(datum.operationShort) * (width / 160)",
          "as": "operationShortLen"
        }
      ]
    },
    {
      "name": "link-data",
      "source": "dataset",
      "transform": [
        {"type": "filter", "expr": "datum.srcid != null"},
        {
          "type": "project",
          "fields": ["srcid", "dstid", "srcid", "dstid"], // Project srcid and dstid twice
          "as": ["source", "target", "edgeSrcId", "edgeDstId"] // Alias the second set for labels
        }
      ]
    }
  ],
  "legends": [
    {
      "direction": "vertical",
      "legendX": 1050,
      "legendY": 0,
      "orient": "none",
      "stroke": "color",
      "fill": "color",
      "titleColor": "#b6bcdd",
      "padding": 10,
      "encode": {
        "title": {"update": {"fontSize": {"value": 14}}},
        "symbols": {"update": {"fillOpacity": {"value": 0.4}}},
        "labels": {
          "update": {"fontSize": {"value": 12}, "fill": {"value": "#b6bcdd"}}
        }
      }
    }
  ],
  "scales": [
    {
      "name": "xscale",
      "type": "band",
      "domain": {"data": "node-data", "field": "Level", "sort": true},
      "paddingOuter": 1,
      "range": "width"
    },
    {
      "name": "yscale",
      "type": "band",
      "domain": {"data": "node-data", "field": "index", "sort": true},
      "range": "height"
    },
    {
      "name": "color",
      "type": "ordinal",
      "domain": {"data": "node-data", "field": "operationType"},
      "range": {"scheme": "tableau10"}
    },
    {
      "name": "size",
      "domain": {"data": "node-data", "field": "Records"},
      "range": [300, 2000]
    }
  ],
  "marks": [
    {
      "name": "nodes",
      "type": "symbol",
      "zindex": 1,
      "from": {"data": "node-data"},
      "on": [
        {
          "trigger": "fix",
          "modify": "node",
          "values": "fix === true ? {fx: node.x, fy: node.y} : {fx: fix[0], fy: fix[1]}"
        },
        {"trigger": "!fix", "modify": "node", "values": "{fx: null, fy: null}"}
      ],
      "encode": {
        "enter": {
          "size": {"scale": "size", "field": "Records"},
          "stroke": {"scale": "color", "field": "operationType"},
          "fill": {"scale": "color", "field": "operationType"},
          "fillOpacity": {"value": 0.1},
          "xfocus": {"scale": "xscale", "field": "Level", "band": 0.5},
          "yfocus": {"scale": "yscale", "field": "index", "band": 0.5},
          "tooltip": {
            "signal": "{OperationType: datum.operationType, Operation: datum.Operation, Records: datum.Records}"
          }
        },
        "update": {"cursor": {"value": "pointer"}}
      },
      "transform": [
        {
          "type": "force",
          "iterations": 300,
          "restart": {"signal": "restart"},
          "static": {"signal": "static"},
          "signal": "force",
          "forces": [
            {"force": "center", "x": {"signal": "cx"}, "y": {"signal": "cy"}},
            {"force": "collide", "radius": {"signal": "nodeRadius"}},
            {"force": "nbody", "strength": {"signal": "nodeCharge"}},
            {
              "force": "link",
              "links": "link-data",
              "distance": {"signal": "linkDistance"},
              "id": "index"
            },
            {"force": "x", "x": "xfocus", "strength": {"signal": "gravityX"}},
            {"force": "y", "y": "yfocus", "strength": {"signal": "gravityY"}}
          ]
        }
      ]
    },
    {
      "type": "path",
      "from": {"data": "link-data"},
      "interactive": false,
      "encode": {
        "update": {"stroke": {"value": "#ccc"}, "strokeWidth": {"value": 0.5}}
      },
      "transform": [
        {
          "type": "linkpath",
          "require": {"signal": "force"},
          "shape": "line",
          "sourceX": "datum.source.x",
          "sourceY": "datum.source.y",
          "targetX": "datum.target.x",
          "targetY": "datum.target.y"
        }
      ]
    },
    {
      "type": "text",
      "name": "labels",
      "from": {"data": "nodes"},
      "zindex": 2,
      "interactive": false,
      "enter": {},
      "encode": {
        "update": {
          "fill": {"signal": "''white''"},
          "y": {"field": "y"},
          "x": {"field": "x"},
          "text": {"field": "datum.operationShort"},
          "align": {"value": "left"},
          "fontSize": {"value": 10},
          "baseline": {"value": "top"},
          "limit": {"signal": "clamp(60, 1, 1000)"},
          "ellipsis": {"value": " "}
        }
      }
    }
    // {
    //   "type": "text",
    //   "name": "edge-labels",
    //   "from": {"data": "link-data"},
    //   "zindex": 2,
    //   "interactive": false,
    //   "encode": {
    //     "update": {
    //       "fill": {"value": "#ADD8E6"},
    //       "x": {"signal": "(datum.source.x + datum.target.x) / 2"},
    //       "y": {"signal": "(datum.source.y + datum.target.y) / 2"},
    //       "text": {"signal": "datum.edgeSrcId + ''->'' + datum.edgeDstId"},
    //       "align": {"value": "center"},
    //       "fontSize": {"value": 9},
    //       "baseline": {"value": "bottom"}
    //     }
    //   }
    // }
  ]
}'

'{
  "autosize": {
    "contains": "padding",
    "type": "fit"
  },
  "view": {
    "stroke": "transparent"
  },
  "font": "Segoe UI",
  "arc": {},
  "area": {
    "line": true,
    "opacity": 0.6
  },
  "bar": {},
  "line": {
    "strokeWidth": 3,
    "strokeCap": "round",
    "strokeJoin": "round"
  },
  "point": {
    "filled": true,
    "size": 75
  },
  "rect": {},
  "text": {
    "font": "Segoe UI",
    "fontSize": 12,
    "fill": "#605E5C"
  },
  "axis": {
    "ticks": false,
    "grid": false,
    "domain": false,
    "labelColor": "#605E5C",
    "labelFontSize": 12,
    "titleFont": "din",
    "titleColor": "#252423",
    "titleFontSize": 16,
    "titleFontWeight": "normal"
  },
  "axisQuantitative": {
    "tickCount": 3,
    "grid": true,
    "gridColor": "#C8C6C4",
    "gridDash": [
      1,
      5
    ],
    "labelFlush": false
  },
  "axisX": {
    "labelPadding": 5
  },
  "axisY": {
    "labelPadding": 10
  },
  "header": {
    "titleFont": "din",
    "titleFontSize": 16,
    "titleColor": "#252423",
    "labelFont": "Segoe UI",
    "labelFontSize": 13.333333333333332,
    "labelColor": "#605E5C"
  },
  "legend": {
    "titleFont": "Segoe UI",
    "titleFontWeight": "bold",
    "titleColor": "#605E5C",
    "labelFont": "Segoe UI",
    "labelFontSize": 13.333333333333332,
    "labelColor": "#605E5C",
    "symbolType": "circle",
    "symbolSize": 75
  },
  "path": {},
  "shape": {},
  "symbol": {
    "strokeWidth": 1.5,
    "size": 50
  }
}'
